#
# Structure for table "h_cube_cms_article_table"
#

DROP TABLE IF EXISTS `h_cube_cms_article_table`;
CREATE TABLE `h_cube_cms_article_table` (
  `article_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章唯一编号',
  `article_title` varchar(40) NOT NULL DEFAULT '' COMMENT '文章标题，40字以内',
  `article_author_id` int(11) DEFAULT NULL COMMENT '文章作者id',
  `article_from` varchar(1024) DEFAULT NULL COMMENT '文章来源',
  `article_content` longtext COMMENT '文章内容',
  `article_publish_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '文章发布时间',
  `article_click_rate` int(11) DEFAULT '1' COMMENT '文章点击率',
  `article_status` varchar(30) NOT NULL DEFAULT 'draft' COMMENT '文章的当前状态',
  `article_category` varchar(30) NOT NULL DEFAULT 'no_category' COMMENT '文章分类',
  `article_second_category` varchar(30) DEFAULT NULL COMMENT '文章二级分类',
  `article_lang` varchar(8) NOT NULL DEFAULT 'global' COMMENT '设置文章的语言，用于i18n',
  `article_licence` varchar(30) NOT NULL DEFAULT 'by-nc-sa40' COMMENT '文章版权',
  `article_if_top` varchar(20) DEFAULT NULL COMMENT '置顶文章',
  `article_if_favor` varchar(20) DEFAULT NULL COMMENT '精选文章',
  `article_if_private` varchar(20) DEFAULT NULL COMMENT '隐私文章',
  `article_if_comment` varchar(20) DEFAULT 'true' COMMENT '文章是否允许评论',
  PRIMARY KEY (`article_id`),
  KEY `article_author_id` (`article_author_id`),
  KEY `article_publish_time` (`article_publish_time`),
  KEY `article_category` (`article_category`),
  KEY `article_lang` (`article_lang`),
  KEY `article_if_top` (`article_if_top`),
  KEY `article_if_favor` (`article_if_favor`),
  KEY `article_if_private` (`article_if_private`),
  KEY `article_status` (`article_status`),
  FULLTEXT KEY `article_content` (`article_content`)
) ENGINE=MyISAM AUTO_INCREMENT=29 DEFAULT CHARSET=utf8 COMMENT='CMS表，存储文章相关信息';

