/*!40000 ALTER TABLE `h_cube_cms_article_table` DISABLE KEYS */;
INSERT INTO `h_cube_cms_article_table` VALUES (6,'魔法帽礼包七折惊喜 情侣更享圣诞优惠',1,'https://popkart.tiancity.com/homepage/article/2017/12/13/54130.html','\n                           <p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">圣诞节来临，神秘老人换上了大家熟悉的红衣服，没错他的真正身份就是圣诞老人！在这特别的节日里，圣诞老人为车手们带来了不一样的惊喜！深受车手们喜爱的魔法帽礼包不只重磅升级了奖励道具，更有<span>1+1</span>的圣诞折扣活动送给车手们多一份的幸运！此外，圣诞像素娃娃机、天降财运等活动也将同步开启，圣诞老人将用最特别的惊喜来回应车手们的期待！<span></span></span>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">精彩活动传送门<span>&gt;&gt;&gt;</span></span></b><a href=\"https://popkart.tiancity.com/homepage/event/2017/kr1221act\" target=\"_blank\"><b><span style=\"font-size:10.0pt;font-family:&quot;color:blue;\">https://popkart.tiancity.com/homepage/event/2017/kr1221act</span></b></a><b><span style=\"font-size:10.0pt;font-family:宋体;color:blue;\"></span></b>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">魔法帽重磅升级 圣诞狂欢<span>1+1</span>活动开启 <span></span></span></b>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">圣诞节对繁荣山丘车手们来说，最大的惊喜就属<span>12</span>月<span>21</span>日<span>6</span>点开始的魔法帽重磅升级了！本次魔法帽内不但将加入红旗<span> 9 </span>烈焰深红、佐罗尼欧、皮蛋超人等超人气道具，更将额外增加一个金框稀有道具！而与此同时，魔法帽礼包<span>1+1</span>活动也将在<span>12</span>月<span>21</span>日<span>6</span>点至<span>1</span>月<span>1</span>日<span>0</span>点前开启，想要红旗<span> 9 </span>烈焰深红、皮蛋超人、佐罗尼欧吗？就来参加吧！ <span></span></span>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"center\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\"><img src=\"/img/article_img/news9.jpg\" alt=\"\"><span></span></span>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">冰雪季天降财运 超级大奖花落谁家<span></span></span></b>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">冰雪季除了堆雪人、打雪仗这些经典活动，还有专属于繁荣山丘的福利活动<span>—</span>天降财运！<span>12</span>月<span>21</span>日至<span>12</span>月<span>28</span>日，每晚<span>18:00~22:00</span>将开启天降财运活动，向活动期间在线的车手们发放魔法手杖<span>300</span>个、魔法手杖<span>200</span>个、魔法手杖<span>100</span>个、圣诞袜<span>1</span>个其中之一作为奖品。活动期间，凡是在线就能获得礼物哦！<span></span></span>\n</p>\n\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">启动像素娃娃机 缤纷好礼送不停<span></span></span></b>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">12</span><span style=\"font-size:10.0pt;font-family:宋体;\">月<span>21</span>日<span>6</span>点至<span>1</span>月<span>4</span>日<span>6</span>点前，圣诞像素娃娃机启动，想要什么都能凭真本事抓到！娃娃机里藏有各式稀有道具，不只有骷髅骑兵<span>9</span>、黑狐<span>9</span>、阿星等高人气车辆角色，更有金色礼盒、银色部件箱、<span>TOP 50 </span>启动器等热门道具。活动期间，只要完成任务就可获得像素代币，而像素代币是启动娃娃机的唯一途径，千万不要错过！<span></span></span>\n</p>\n\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">既然圣诞老人的身份已经被揭晓，那接下来他就要忙着为繁荣山丘的车手分发礼物，准备更多惊喜。如果想理解更多的圣诞活动的话，欢迎登录《跑跑卡丁车》来一窥究竟！<span><span>&nbsp; </span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">&nbsp;</span>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">跑跑卡丁车<span> 2017</span>年<span>12</span>月<span>21</span>日<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span>\n</p>\n','2017-11-22 19:40:36',41,'published','资讯公告','最新活动','global','repost','top',NULL,NULL,'true'),(8,'圣诞节惊现魔法帽 谁能成为圣诞挑战王',1,NULL,'\n                           <p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">圣诞节前夕，一个神秘的老人为繁荣山丘的车手们带来了充满神秘气息的魔法帽，据说魔法帽中加了点特别的私货，这些私货用料十足充满惊喜。随着老人的到来，一场竞争激烈的挑战赛即将拉开帷幕，老人会在击败他的勇士中挑选前<span>500</span>位赠送宝宝徽章及其他丰厚奖励！究竟谁能击败神秘老人，请拭目以待！<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">精彩活动传送门<span>&gt;&gt;&gt;</span></span></b><a href=\"https://popkart.tiancity.com/homepage/event/2017/kr1130act\" target=\"_blank\"><b><span style=\"font-size:10.0pt;font-family:\" color:blue;\"=\"\">https://popkart.tiancity.com/homepage/event/2017/kr1130act</span></b></a><b><span style=\"font-size:10.0pt;font-family:宋体;color:blue;\"></span></b> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">魔法帽神秘上架 内含多款全新道具 <span></span></span></b> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">11</span><span style=\"font-size:10.0pt;font-family:宋体;\">月<span>30</span>日<span>6</span>点至<span>12</span>月<span>28</span>日<span>6</span>点前，老人带来的魔法帽具有神秘而独特的气息，只有用同样富有魔力的魔法棒才能打开，内含的各种道具更是散发着浓浓的圣诞气息。例如毒刺<span> 9XE</span>、冰精灵茜茜、冰精灵头饰等道具都很适合在圣诞节上场！魔法帽内的惊喜可不止于此，更有游侠<span>9</span>、黯泣<span> 9</span>、飞行导弹钻石等深受车手们喜爱的车辆道具也会在其中出现！快来许下的心愿吧！<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"center\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\"><img src=\"/img/article_img/news8.jpg\" alt=\"\"><span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">圣诞袜惊喜不断 谁才是真正的圣诞挑战王<span></span></span></b> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">圣诞节怎么能缺少藏有惊喜的圣诞袜呢，本次更新后车手们通过完成“圣诞节大战<span>AI</span>”任务即可获得内含圣诞节护目镜、滑雪手杖、圣诞节礼物手杖等超人气道具的圣诞袜！此外，这位神秘的老人还准备了一场真正的挑战赛等待着车手们。通过一次次打败圣诞战队，逐渐爬上排行榜前<span>500</span>的行列，就能成为圣诞挑战王！当然成为圣诞挑战王更能收获神秘老人为之准备的宝宝徽章、<span>TOP\n30 </span>启动器、<span>100</span>酷币、金色部件箱哟！ <span></span></span> \n</p>\n\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">猜到这位神秘老人的身份了吗？如果还没猜到的话，赶快登录《跑跑卡丁车》来到繁荣山丘一探究竟吧！还会有更多惊喜等待着车手们！<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">&nbsp;</span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">跑跑卡丁车<span>11</span>月<span>30</span>日<span></span></span> \n</p>','2017-11-23 09:50:02',36,'published','资讯公告','最新活动','global','repost','top',NULL,NULL,'true'),(9,'圣诞狂欢正式开启 魔法帽礼包1+1',1,NULL,'\n                           <p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">圣诞节来临，神秘老人换上了大家熟悉的红衣服，没错他的真正身份就是圣诞老人！在这特别的节日里，圣诞老人为车手们带来了不一样的惊喜！深受车手们喜爱的魔法帽礼包不只重磅升级了奖励道具，更有<span>1+1</span>的圣诞折扣活动送给车手们多一份的幸运！此外，圣诞像素娃娃机、天降财运等活动也将同步开启，圣诞老人将用最特别的惊喜来回应车手们的期待！<span></span></span>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">精彩活动传送门<span>&gt;&gt;&gt;</span></span></b><a href=\"https://popkart.tiancity.com/homepage/event/2017/kr1221act\" target=\"_blank\"><b><span style=\"font-size:10.0pt;font-family:&quot;color:blue;\">https://popkart.tiancity.com/homepage/event/2017/kr1221act</span></b></a><b><span style=\"font-size:10.0pt;font-family:宋体;color:blue;\"></span></b>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">魔法帽重磅升级 圣诞狂欢<span>1+1</span>活动开启 <span></span></span></b>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">圣诞节对繁荣山丘车手们来说，最大的惊喜就属<span>12</span>月<span>21</span>日<span>6</span>点开始的魔法帽重磅升级了！本次魔法帽内不但将加入红旗<span> 9 </span>烈焰深红、佐罗尼欧、皮蛋超人等超人气道具，更将额外增加一个金框稀有道具！而与此同时，魔法帽礼包<span>1+1</span>活动也将在<span>12</span>月<span>21</span>日<span>6</span>点至<span>1</span>月<span>1</span>日<span>0</span>点前开启，想要红旗<span> 9 </span>烈焰深红、皮蛋超人、佐罗尼欧吗？就来参加吧！ <span></span></span>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"center\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\"><img src=\"/img/article_img/news9.jpg\" alt=\"\"><span></span></span>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">冰雪季天降财运 超级大奖花落谁家<span></span></span></b>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">冰雪季除了堆雪人、打雪仗这些经典活动，还有专属于繁荣山丘的福利活动<span>—</span>天降财运！<span>12</span>月<span>21</span>日至<span>12</span>月<span>28</span>日，每晚<span>18:00~22:00</span>将开启天降财运活动，向活动期间在线的车手们发放魔法手杖<span>300</span>个、魔法手杖<span>200</span>个、魔法手杖<span>100</span>个、圣诞袜<span>1</span>个其中之一作为奖品。活动期间，凡是在线就能获得礼物哦！<span></span></span>\n</p>\n\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">启动像素娃娃机 缤纷好礼送不停<span></span></span></b>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">12</span><span style=\"font-size:10.0pt;font-family:宋体;\">月<span>21</span>日<span>6</span>点至<span>1</span>月<span>4</span>日<span>6</span>点前，圣诞像素娃娃机启动，想要什么都能凭真本事抓到！娃娃机里藏有各式稀有道具，不只有骷髅骑兵<span>9</span>、黑狐<span>9</span>、阿星等高人气车辆角色，更有金色礼盒、银色部件箱、<span>TOP 50 </span>启动器等热门道具。活动期间，只要完成任务就可获得像素代币，而像素代币是启动娃娃机的唯一途径，千万不要错过！<span></span></span>\n</p>\n\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">既然圣诞老人的身份已经被揭晓，那接下来他就要忙着为繁荣山丘的车手分发礼物，准备更多惊喜。如果想理解更多的圣诞活动的话，欢迎登录《跑跑卡丁车》来一窥究竟！<span><span>&nbsp; </span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">&nbsp;</span>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">跑跑卡丁车<span> 2017</span>年<span>12</span>月<span>21</span>日<span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span>\n</p>\n','2017-11-23 12:42:40',139,'published','资讯公告','最新活动','global','repost','top',NULL,NULL,'true'),(10,'能源启动器礼包怀旧上架 经典RP模式大受追捧',1,NULL,'\n                           <p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">随着本次感恩节版本更新，能源启动器礼包也将怀旧上架，内含五大车系所有经典车款，一次让车手们收获所有经典好车。同时，在本次更新后，经典<span>RP</span>模式将重新启动，车手们也可以在本次开启的经典<span>RP</span>模式中，体验经典车辆的风采，重温<span>9</span>系车带来的感动。接下来，就一起看看还有哪些精彩活动吧！<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">精彩活动传送门<span>&gt;&gt;&gt;</span></span></b><a href=\"https://popkart.tiancity.com/homepage/event/2017/kr1123act/event/\" target=\"_blank\"><span>https://popkart.tiancity.com/homepage/event/2017/kr1123act/event/</span></a><b><span style=\"font-size:10.0pt;font-family:宋体;color:blue;\"></span></b> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">能源启动器礼包怀旧上架 尽揽五大车系经典好车 <span></span></span></b> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">一直以来，五大车系每出新车必定成为车手们关注的焦点。<span>11</span>月<span>23</span>日<span>6</span>点至<span>11</span>月<span>30</span>日<span>6</span>点前，上架的能源启动器礼包更是深受车手们青睐。打开能源启动器礼包，将成为车手们收获马拉松<span> 9 LE</span>、棉花糖<span> 9 LE</span>、棉花糖<span> 9\nXE</span>等稀有靓车的大好契机！机会不容错过，赶快行动起来！<span></span></span> \n</p>\n\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">经典<span>RP</span>模式开启 参与活动更有好礼相送<span></span></span></b> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">每次开局都在期待一份未知的惊喜，撩动着车手们心弦的正是经典<span>RP</span>模式的开启！<span>11</span>月<span>23</span>日<span>6</span>点至<span>11</span>月<span>30</span>日<span>6</span>点前，经典<span>RP</span>模式正式开启！选手们可以在频道选择道具<span>RP</span>个人赛<span>/</span>组队赛、无限加速<span>RP</span>个人赛<span>/</span>组队赛来参与经典<span>RP</span>模式，完成相关任务更能获得能源启动器、能源礼盒、道具变更卡等丰厚奖励！<span></span></span> \n</p>\n\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">皮蛋与宝宝为了感谢车手们的衷情陪伴，为大家准备了特别的感恩节礼物，赶快登录《跑跑卡丁车》来看看会获得怎么样的感恩节礼物吧！</span>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<br>\n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t跑跑卡丁车 2017年11月23日\n</p>\n ','2017-11-23 12:43:53',15,'published','资讯公告','赛事资讯','global','repost','top',NULL,NULL,'true'),(11,'2017 TGG冬季杯总决赛11月18日震撼上演 群雄逐鹿剑指王者',1,NULL,' <p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\n\t<span style=\"font-family:;\">2017 TGG</span><span style=\"font-family:;\">冬季杯小组赛已经圆满落幕。参加小组赛的队伍经过多场比赛的厮杀，最终决出了决赛名额。<span>11</span>月<span>18</span>日本周六<span>13</span>：<span>00</span>，<span>2017TGG</span>冬季杯总决赛正式开启，让我们用最大的热情来吹散明日的严寒吧！<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\n\t<span style=\"font-family:;\">小组赛阶段最精彩的对决要数《跑跑卡丁车》|孤君战队|和|<span>TB|</span>之间的对决了，这场比赛吸引了无数人的眼球。<span>TB</span>车队在比赛中的表现可圈可点，但在一些关键路段的微调处理与团队位置转变意识上略逊一筹，最终以<span>3:5</span>和<span>1:5</span>的比分遗憾落败。而老牌劲旅|苏荷车队|与超新星车队 - |TB未来星|均止步于半决赛。虽然都十分的可惜，但这就是比赛！如此现实与残酷。希望两支车队能总结此次比赛的经验与得失，在将来能取得更多更好的战果。<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\n\t<span style=\"font-family:;\">总决赛阶段《跑跑卡丁车》更会带来梦幻般的个人赛，<span>8</span>名职业选手互相对抗角逐势必会给玩家们带来更加激情的观赏体验。</span> \n</p>\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\n\t<img src=\"/img/article_img/news11_1.jpg\" alt=\"\"><br>\n<span style=\"font-family:;\"><span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\n\t<b><span style=\"font-family:;\">2017TGG</span></b><b><span style=\"font-family:;\">冬季杯总决赛小组赛名次如下：<span></span></span></b> \n</p>\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\n\t<span style=\"font-family:;\">跑跑卡丁车名次<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\n\t<span style=\"font-family:\" color:black;\"=\"\">第一名：孤君战队<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\n\t<span style=\"font-family:\" color:black;\"=\"\">第二名：<span>TB</span>战队<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\n\t<span style=\"font-family:;\">第三名：苏荷战队<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\n\t<span style=\"font-family:;\">第四名：<span>TB</span>未来星战队<span></span></span> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\">赛点城市： 上海<span></span></span> \n</p>\n\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\">决赛直播时间：<span>2017</span>年<span>11</span>月<span>18</span>日<span>13:00</span></span> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\">比赛地址：上海灵石路<span>709</span>号产业园<span>39</span>号棚<span></span></span> \n</p>\n\n<p class=\"MsoNormal\">\n\t<b><span style=\"font-family:;\">直播地址：<span></span></span></b> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\">战旗直播：<span>http://www.zhanqi.tv/tiancity</span></span> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\">火猫直播：<span>https://www.huomao.com/1358</span></span> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\">龙珠直播：<span>http://star.longzhu.com/xiaolu</span></span> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\">BiliBili</span><span style=\"font-family:;\">直播：<span>http://live.bilibili.com/767886</span></span> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\">虎牙直播：<span>http://www.huya.com/1003686026</span></span> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\">全民直播：<span>https://www.quanmin.tv/22548900</span></span> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\">熊猫直播：<span>https://www.panda.tv/354223</span></span> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\">乐视直播：<span>http://games.le.com/izt/tgg/</span></span> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\"><span><br>\n</span></span> \n</p>\n<p class=\"MsoNormal\">\n\t<span style=\"font-family:;\"><span>跑跑卡丁车&nbsp; 2017年11月17日<br>\n</span></span> \n</p>\n','2017-11-23 17:44:19',28,'published','资讯公告','赛事资讯','global','repost','top',NULL,NULL,'true'),(12,'等离子 9 FT 横空出世 老玩家回归好礼相送',1,NULL,'<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">上周的单身派对，伴随各种优惠活动想必让车手们度过了一个愉快的光棍节。但是狂欢的热潮不止于此，<span>11</span>月<span>16</span>日起，车手们千呼万唤的强化礼包折扣活动将再度开启，同时等离子专属礼包也将超值上架！另外，随着激情四射的等离子<span>GP</span>赛的开启，热闹的繁荣山丘赛车协会也进行了一波车手召集。不管是老玩家回归，还是新司机上路，都可以获得由赛车协会精心准备的狂飙大礼包！<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">精彩活动传送门<span>&gt;&gt;&gt;</span></span></b><b><span style=\"font-size:10.0pt;font-family:宋体;color:blue;\"> </span></b><a href=\"https://popkart.tiancity.com/homepage/event/2017/kr1116act/item\" target=\"_blank\"><span></span></a><a href=\"https://popkart.tiancity.com/homepage/event/2017/kr1116act/item\" target=\"_blank\"><b><span style=\"font-size:10.0pt;font-family:宋体;\"><span>https://popkart.tiancity.com/homepage/event/2017/kr11</span></span></b><b><span style=\"font-size:10.0pt;font-family:宋体;\"><span>16</span></span></b><b><span style=\"font-size:10.0pt;font-family:宋体;\"><span>act/item</span></span></b></a><b><span style=\"font-size:10.0pt;font-family:宋体;color:blue;\"></span></b> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">等离子专属礼包上架 等离子<span>GP</span>赛同步开启 <span></span></span></b> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">本次更新，等离子专属礼包超值上架，内含等离子<span> 9 FT</span>（无限制）、金色部件箱（<span>1</span>个）、粒子激活器<span>S</span>（<span>3</span>个）、<span>TOP 30 </span>启动器（<span>1</span>个）。<span>11</span>月<span>16</span>日<span>6</span>点至<span>11</span>月<span>22</span>日<span>24</span>点前，等离子<span>GP</span>赛也将拉开帷幕，获得金牌、银牌的车手们就能获得稀有车辆等离子<span> 9 PT</span>及等离子专属徽章。赶快驾驶你的等离子<span> 9 FT</span>来一决胜负吧！<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"center\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\"><img src=\"/img/article_img/news12_1.jpg\" alt=\"\"><span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">强化礼包限时折扣 打造专属爱车<span></span></span></b> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">11</span><span style=\"font-size:10.0pt;font-family:宋体;\">月<span>16</span>日<span>6</span>点至<span>11</span>月<span>30</span>日<span>6</span>点前，强化礼包限时折扣活动开启，只需原价<span>70%</span>的电池，即可兑换！强化礼包<span>S</span>、强化礼包<span>R</span>、强化礼包<span>Black</span>内所含的改装道具都是车手们通往车神之路上必不可少的！快来打造一款专属于你的爱车吧！<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"center\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\"><img src=\"/img/article_img/news12_2.jpg\" alt=\"\"><span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<b><span style=\"font-size:10.0pt;font-family:宋体;\">新老司机上路得好礼 福利满满欢乐漂移<span></span></span></b> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">11</span><span style=\"font-size:10.0pt;font-family:宋体;\">月<span>16</span>日<span>6</span>点至<span>11</span>月<span>30</span>日<span>6</span>点前，繁荣山丘赛车协会招募有能力的赛车手加入，为此特别准备了一份超级大礼。礼包内含游侠<span> 9</span>（<span>30</span>天）<span>, </span>阿努比斯<span> 9</span>（<span>30</span>天）<span>, </span>像素皮蛋（<span>30</span>天）<span>, </span>飞行员太阳镜（<span>30</span>天）<span>,</span>女巫帽头饰（<span>30</span>天）等精美又实用的道具！还等什么呢，快加入繁荣山丘赛车协会，一起快乐漂移吧！<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"center\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\"><img src=\"/img/article_img/news12_3.jpg\" alt=\"\"><span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;text-indent:20.0pt;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">繁荣山丘每天都在展开一场场速度的对决，车手们准备好了吗！登录《跑跑卡丁车》报名等离子<span>GP</span>赛，这是一个证明自己实力的绝佳时机！<span></span></span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">&nbsp;</span> \n</p>\n<p class=\"MsoNormal\" style=\"text-align:left;\" align=\"left\">\n\t<span style=\"font-size:10.0pt;font-family:宋体;\">跑跑卡丁车<span>11</span>月<span>16</span>日<span></span></span> \n</p>\n','2017-12-08 06:40:28',122,'published','资讯公告','最新活动','global','repost','top',NULL,NULL,'true'),(13,'12月21日全区例行维护公告[已结束]',1,'https://popkart.tiancity.com/homepage/article/2017/12/20/54187.html',' <p class=\"MsoNorma