DROP TABLE IF EXISTS `h_cube_note_content_table`;
CREATE TABLE `h_cube_note_content_table` (
  `note_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '笔记唯一编号',
  `note_submit_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '提交时间',
  `note_author` varchar(20) NOT NULL DEFAULT '' COMMENT '作者',
  `note_color_tag` varchar(24) NOT NULL DEFAULT '' COMMENT '颜色标签',
  `note_content` longtext NOT NULL COMMENT '笔记内容',
  `note_status` char(20) DEFAULT NULL COMMENT '笔记状态，为空则公开，recycled为待删除，private为不公开',
  `note_if_deny_comment` bit(1) DEFAULT NULL COMMENT '是否允许评论？空则允许，否则不允许',
  PRIMARY KEY (`note_id`),
  KEY `note_color_tag` (`note_color_tag`),
  KEY `note_status` (`note_status`),
  KEY `note_submit_time` (`note_submit_time`),
  KEY `note_author` (`note_author`)
) ENGINE=MyISAM AUTO_INCREMENT=705 DEFAULT CHARSET=utf8 COMMENT='笔记存储表';

