#
# Structure for table "h_cube_passport_center"
#

DROP TABLE IF EXISTS `h_cube_passport_center`;
CREATE TABLE `h_cube_passport_center` (
  `user_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '用户ID',
  `user_name` varchar(20) NOT NULL DEFAULT '' COMMENT '用户名',
  `user_password` char(40) NOT NULL DEFAULT '' COMMENT '用户密码',
  `user_salt` varchar(40) NOT NULL DEFAULT '' COMMENT '用户密码盐值',
  `user_group` varchar(40) DEFAULT NULL COMMENT '用户组标识',
  `user_email` varchar(255) NOT NULL DEFAULT '' COMMENT '用户邮箱',
  `user_status` varchar(255) DEFAULT NULL COMMENT '用户状态',
  `user_sign_up_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '用户注册时间',
  `user_last_sign_in_time` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '用户上一次登录时间',
  PRIMARY KEY (`user_id`,`user_name`),
  UNIQUE KEY `user_email` (`user_email`),
  UNIQUE KEY `user_name` (`user_name`),
  KEY `user_group` (`user_group`)
) ENGINE=InnoDB AUTO_INCREMENT=66 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='网站用户表';

