DROP TABLE IF EXISTS `h_cube_admin_list`;
CREATE TABLE `h_cube_admin_list` (
  `admin_id` int(6) NOT NULL AUTO_INCREMENT COMMENT '管理员工号',
  `admin_group` varchar(3) NOT NULL DEFAULT '0' COMMENT '管理员组别',
  `admin_status` varchar(20) NOT NULL DEFAULT 'false' COMMENT '管理员状态',
