  CONSTRAINT `admin_unique_name` FOREIGN KEY (`admin_passport_name`) REFERENCES `h_cube_passport_center` (`user_name`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=1003 DEFAULT CHARSET=utf8 COMMENT='CMS管理员用户表';

